    //     })
    //         .then(response => {
    //             return response.text();
    //         })
    //         .then(data => {
    //             alert(data);
    //             getUser();
    //         });
    // }

    return (
        <div className={`${classes.AppMain}`}>
            <img src={logo} className={`${classes.logo}`} alt="logo"/>
            <p>
                I am <span>{user.name}</span> and my roles are: <span>{props.user.roles.join(', ')}</span>
            </p>

            {/*<button onClick={() => updateUser(user.id)} className={classes.updateButton}>Update</button>*/}
        </div>
    );
}

export default Profile;
