import * as Yup from "yup";
import {ErrorMessage, Field, Form, Formik} from "formik";
import {authenticationService} from "../../services/authentication.service";
import React from "react";


class Login extends React.Component {
    constructor(props) {
        super(props);

        // redirect to home if already logged in
        if (authenticationService.currentUserValue) {
            this.props.history.push('/');
        }
    }

    // function authenticate() {
    //     fetch('http://localhost:3001/authenticate', {
    //         method: 'POST',
    //         headers: {
    //             'Content-Type': 'application/json',
    //         },
    //         body: JSON.stringify({id}),
    //     })
    //         .then(response => {
    //             return response.text();
    //         })
    //         .then(data => {
    //             alert(data);
    //             getUsers();
    //         });
    // }

    render() {
        return (
            <div>
                <div className="alert alert-info">
                    <strong>Normal User</strong> - id: 2<br/>
                    <strong>Administrator</strong> - id: 1
                </div>

                <h2>Login</h2>

                {/*<form>*/}
                {/*    <div className="form-group">*/}
                {/*        <label htmlFor="id">ID</label>*/}
                {/*        <input name="id" type="text"/>*/}
                {/*    </div>*/}
                {/*    <div className="form-group">*/}
                {/*        <button className="btn btn-primary" onClick={authenticate}>Login</button>*/}
                {/*    </div>*/}
                {/*</form>*/}

                <Formik
                    initialValues={{
                        id: ''
                    }}
                    validationSchema={Yup.object().shape({
                        id: Yup.string().required('ID is required')
                    })}
                    onSubmit={({id}, {setStatus, setSubmitting}) => {
                        setStatus();
                        authenticationService.login(id)
                            .then(
                                user => {
                                    const {from} = this.props.location.state || {from: {pathname: "/"}};
                                    this.props.history.push(from);
                                },
                                error => {
                                    setSubmitting(false);
                                    setStatus(error);
                                }
                            );
                    }}
                    render={({errors, status, touched, isSubmitting}) => (
                        <Form>
                            <div className="form-group">
                                <label htmlFor="id">ID</label>
                                <Field name="id" type="text"
                                       className={'form-control' + (errors.id && touched.id ? ' is-invalid' : '')}/>
                                <ErrorMessage name="id" component="div" className="invalid-feedback"/>
                            </div>
                            <div className="form-group">
                                <button type="submit" className="btn btn-primary" disabled={isSubmitting}>Login</button>
                                {isSubmitting &&
                                    <img
                                        src="data:image/gif;base64,"/>
                                }
                            </div>
                            {status &&
                                <div className={'alert alert-danger'}>{status}</div>
                            }
                        </Form>
                    )}
                />
            </div>
        );
    }

}

export {Login};
